{
    "name": "Demo of Webhook + MQTT - Subscription Filtering",
    "apiVersion": "1.0.0",
    "definitionVersion": "4.0.0",
    "type": "async",
    "description": "Demo of Webhook + MQTT - Subscription Filtering",
    "visibility": "PUBLIC",
    "listeners": [
            {
                "type": "subscription",
                "entrypoints": [
                    {
                        "type": "webhook"
                    }
                ]
            }
        ],
    "endpointGroups": [
        {
            "name": "default",
            "type": "mqtt5",
            "endpoints": [
                {
                    "name": "HiveMQ Broker",
                    "type": "mqtt5",
                    "weight": 1,
                    "inheritConfiguration": false,
                    "configuration": {
                        "serverHost": "hivemq",
                        "serverPort": "1883",
                        "topic": "demo",
                        "consumer" : {
                            "enabled": true
                        },
                        "producer": {
                            "enabled": true
                        }
                    }
                }
            ]
        }
    ]
}

{
    "id": "{{api}}",
    "name": "Demo of Webhook + MQTT - Subscription Filtering",
    "apiVersion": "1.0.0",
    "definitionVersion": "4.0.0",
    "type": "async",
    "description": "Demo of Webhook + MQTT - Subscription Filtering",
    "visibility": "PUBLIC",
    "validation": "AUTO",
    "listeners": [
            {
                "type": "subscription",
                "entrypoints": [
                    {
                        "type": "webhook"
                    }
                ]
            }
        ],
    "endpointGroups": [
        {
            "name": "default",
            "type": "mqtt5",
            "endpoints": [
                {
                    "name": "HiveMQ Broker",
                    "type": "mqtt5",
                    "weight": 1,
                    "inheritConfiguration": false,
                    "configuration": {
                        "serverHost": "hivemq",
                        "serverPort": "1883",
                        "topic": "demo",
                        "consumer" : {
                            "enabled": true
                        },
                        "producer": {
                            "enabled": true
                        }
                    }
                }
            ]
        }
    ],
    "flows": [
        {
            "name": "",
            "selectors": [],
            "request": [],
            "response": [],
            "subscribe": [
                {
                    "name": "Message filtering",
                    "description": "Apply filter to messages",
                    "enabled": true,
                    "policy": "message-filtering",
                    "configuration": {
                        "filter": "{#jsonPath(#message.content, '$.feature') == #subscription.metadata.feature}"
                    }
                }
            ],
            "publish": [],
            "enabled": true
        }
    ]
}
